import base64

# Simple calendar icon as base64 PNG (48x48)
icon48_base64 = ""

# Simple calendar icon as base64 PNG (96x96) 
icon96_base64 = ""

# Decode and save
with open('icon-48.png', 'wb') as f:
    f.write(base64.b64decode(icon48_base64))

with open('icon-96.png', 'wb') as f:
    f.write(base64.b64decode(icon96_base64))

print('âœ“ Icons created successfully!')
